  // === Apply layout ===
  const cells = layout.cells;
  for (let ref in cells) {
    const cell = sheet.getRange(ref);
    const data = cells[ref];
    if (data.content) cell.setValue(data.content);
    if (data.formula) cell.setFormula(data.formula);

    const fmt = data.formatting || {};
    if (fmt.fillColor) cell.setBackground(fmt.fillColor);
    if (fmt.font) cell.setFontFamily(fmt.font);
    if (fmt.fontSize) cell.setFontSize(fmt.fontSize);
    if (fmt.fontColor) cell.setFontColor(fmt.fontColor);
    if (fmt.alignment) {
      if (fmt.alignment === "center") cell.setHorizontalAlignment("center");
      if (fmt.alignment === "left") cell.setHorizontalAlignment("left");
      if (fmt.alignment === "right") cell.setHorizontalAlignment("right");
    }
    if (fmt.merge) {
      const range = sheet.getRange(fmt.merge);
      range.merge();
    }
  }

  // === Conditional formatting examples ===
  const rules = sheet.getConditionalFormatRules();
  const greenRule = SpreadsheetApp.newConditionalFormatRule()
    .whenFormulaSatisfied("=$B$3>=0.8")
    .setBackground("#C6EFCE")
    .setRanges([sheet.getRange("C3")])
    .build();
  const yellowRule = SpreadsheetApp.newConditionalFormatRule()
    .whenFormulaSatisfied("=AND($B$3>=0.5,$B$3<0.8)")
    .setBackground("#FFF2CC")
    .setRanges([sheet.getRange("C3")])
    .build();
  const redRule = SpreadsheetApp.newConditionalFormatRule()
    .whenFormulaSatisfied("=$B$3<0.5")
    .setBackground("#F4CCCC")
    .setRanges([sheet.getRange("C3")])
    .build();
  rules.push(greenRule, yellowRule, redRule);
  sheet.setConditionalFormatRules(rules);

  // === Insert placeholder chart ===
  const chart = sheet.newChart()
    .setChartType(Charts.ChartType.PIE)
