    .setOption("title", "Compliance Status Overview")
    .setOption("pieSliceText", "percentage")
    .setOption("colors", ["#4CAF50", "#FFEB3B", "#F44336"])
    .build();
  sheet.insertChart(chart);

  // Adjust column widths
  sheet.setColumnWidths(1, 8, 150);
  sheet.setRowHeights(1, 30, 25);
}
