/**
 * Google Apps Script to build a "Compliance Dashboard" sheet
 * from a provided JSON layout file (Compliance_Dashboard_Layout.json).
 *
 * This script:
 * 1. Creates a new sheet named 'Compliance Dashboard'.
 * 2. Reads layout definitions (cells, merges, formatting, formulas, etc.) from JSON.
 * 3. Applies formatting, merges, and conditional formatting.
 * 4. Inserts a placeholder chart in the defined range (A8:H15).
 */

function buildComplianceDashboard() {
  // Step 1: Load the JSON layout file from Google Drive
  // (Assumes the JSON file is stored in your Google Drive)
  const files = DriveApp.getFilesByName('Compliance_Dashboard_Layout.json');
  if (!files.hasNext()) {
    throw new Error('Compliance_Dashboard_Layout.json not found in Drive.');
  }
  const file = files.next();
  const layout = JSON.parse(file.getBlob().getDataAsString());

  // Step 2: Create or clear the "Compliance Dashboard" sheet
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  let sheet = ss.getSheetByName('Compliance Dashboard');
  if (sheet) {
    ss.deleteSheet(sheet);
  }
  sheet = ss.insertSheet('Compliance Dashboard');

  // Step 3: Apply cell contents, formulas, and formatting
  layout.cells.forEach(cell => {
    const range = sheet.getRange(cell.range);
    if (cell.value !== undefined) range.setValue(cell.value);
    if (cell.formula !== undefined) range.setFormula(cell.formula);

    // Apply formatting if defined
    if (cell.format) {
      const fmt = cell.format;
      if (fmt.fontSize) range.setFontSize(fmt.fontSize);
      if (fmt.fontWeight) range.setFontWeight(fmt.fontWeight);
      if (fmt.fontColor) range.setFontColor(fmt.fontColor);
      if (fmt.background) range.setBackground(fmt.background);
      if (fmt.horizontalAlignment) range.setHorizontalAlignment(fmt.horizontalAlignment);
      if (fmt.verticalAlignment) range.setVerticalAlignment(fmt.verticalAlignment);
    }
  });

  // Step 4: Handle merged cells
  if (layout.merges) {
    layout.merges.forEach(merge => {
      sheet.getRange(merge).merge();
    });
  }

  // Step 5: Apply conditional formatting rules
  if (layout.conditionalFormatting) {
    const rules = [];
    layout.conditionalFormatting.forEach(ruleDef => {
      const range = sheet.getRange(ruleDef.range);
      const rule = SpreadsheetApp.newConditionalFormatRule()
        .whenFormulaSatisfied(ruleDef.formula)
        .setBackground(ruleDef.background)
        .setRanges([range])
        .build();
      rules.push(rule);
    });
    sheet.setConditionalFormatRules(rules);
  }

  // Step 6: Insert a placeholder chart in the defined range (A8:H15)
  const chartRange = sheet.getRange('A8:H15');
  const chart = sheet.newChart()
    .setChartType(Charts.ChartType.COLUMN)
    .addRange(chartRange)
    .setPosition(8, 9, 0, 0)
    .setOption('title', 'Compliance Overview (Placeholder)')
    .build();
  sheet.insertChart(chart);

  // Step 7: Auto-resize columns for better readability
  sheet.autoResizeColumns(1, sheet.getLastColumn());

  // Step 8: Log completion
  Logger.log('Compliance Dashboard successfully created.');
}
